import React, { useState, useEffect } from "react";
import "./style.css";
import { Link } from "react-router-dom";

const Home = () => {
  const [houses, setHouses] = useState([]);
  const [filteredHouses, setFilteredHouses] = useState([]);
  const [design, setDesign] = useState("");
  const [price, setPrice] = useState("");
  const [area, setArea] = useState("");
  const [searchQuery, setSearchQuery] = useState(""); // State for search input
  const [groupedHouses, setGroupedHouses] = useState({}); // Grouped by design (category)
  const [visibleCounts, setVisibleCounts] = useState({}); // Controls number of visible houses per category

  // Fetch houses from backend API
  useEffect(() => {
    fetch("/api/houses")
      .then((response) => response.json())
      .then((data) => {
        if (Array.isArray(data)) {
          setHouses(data);
          setFilteredHouses(data);
        } else {
          console.error("Unexpected data format:", data);
        }
      })
      .catch((error) => console.error("Fetch error:", error));
  }, []);

  // Function to filter houses based on design, price, and area
  const filterHouses = () => {
    const filtered = houses.filter((house) => {
      const housePrice = house.price || null; // Ensure price is available
      const houseArea = house.area || null; // Ensure area is available

      // Convert price range from lakhs to actual numbers
      const [minPrice, maxPrice] = price
        ? price.split("-").map((p) => parseInt(p))
        : [null, null];

      // Parse area range directly as it's a number
      const [minArea, maxArea] = area
        ? area.split("-").map((a) => parseInt(a))
        : [null, null];

      // Check all conditions
      return (
        (!design || house.name === design) &&
        (!price || (housePrice >= minPrice && housePrice <= maxPrice)) &&
        (!area || (houseArea >= minArea && houseArea <= maxArea))
      );
    });

    setFilteredHouses(filtered);
  };

  // Function to handle NLP-based search
  const handleSearch = () => {
    // Update the URL to point to the backend API
    fetch("http://localhost:5000/search", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({ query: searchQuery }), // Send user input as query
    })
      .then((response) => {
        if (!response.ok) {
          throw new Error("Network response was not ok");
        }
        return response.json(); // Parse JSON response
      })
      .then((data) => {
        // Set the filtered houses with the returned search data
        setFilteredHouses(data);
      })
      .catch((error) => console.error("Search error:", error));
  };

 // Function to group houses by their 'design' (category)
  const groupHousesByDesign = (houses) => {
    const grouped = houses.reduce((acc, house) => {
      const category = house.name || "Others"; // Group by house 'name' (design)
      if (!acc[category]) {
        acc[category] = [];
      }
      acc[category].push(house);
      return acc;
    }, {});
    setGroupedHouses(grouped);

    // Initialize visible counts for each category (limit initially to 3)
    const initialVisibleCounts = Object.keys(grouped).reduce((acc, key) => {
      acc[key] = 3;
      return acc;
    }, {});
    setVisibleCounts(initialVisibleCounts);
  };

  // Function to load more houses for a specific category
  const loadMoreHouses = (category) => {
    setVisibleCounts((prevCounts) => ({
      ...prevCounts,
      [category]: prevCounts[category] + 3, // Show 3 more houses when "View More" is clicked
    }));
  };

  // Render houses by category
  const renderCategoryCards = () => {
    return Object.keys(groupedHouses).map((category, index) => (
      <div key={index} className="col-12 mb-4">
        <div className="category-card p-3">
          <h3>{category}</h3>
          <div className="row">
            {groupedHouses[category]
              .slice(0, visibleCounts[category]) // Limit the visible houses
              .map((house, houseIndex) => (
                <div key={houseIndex} className="col-sm-4 col-md-5 col-lg-3 col-xl-3 card">
                  <Link
                    to={`/houseDetails/${house._id}`}
                    className="text-reset text-decoration-none"
                    target="_blank"
                  >
                    <img
                      src={house.images?.exterior?.[0] || "default-image.jpg"} // Ensure there's an exterior image
                      className="card-img-top"
                      alt={house.name}
                    />

                    <div className="card-body">
                      <div className="card-content">
                        <h5>{house.name}</h5>
                        <p>{house.desp}</p>
                      </div>
                      <div className="card-btn text-end">
                        <button className="btn btn-outline-dark">Visit</button>
                      </div>
                    </div>
                  </Link>
                </div>
              ))}
          </div>
          <div className="row mt-3">
            <div className="col-6">
              <button
                className="btn btn-secondary"
                onClick={() => loadMoreHouses(category)}
              >
                View More
              </button>
            </div>
            <div className="col-6 text-end">
              <Link to={`/category/${category}`} className="btn btn-primary">
                View All
              </Link>
            </div>
          </div>
        </div>
      </div>
    ))
  }; 

  return (
    <main className="col-sm-12 col-md-12 col-lg-12 col-xl-12 main p-5">
      <div className="text-center title-text">
        A complete solution for Planning & Designing your dream Home
      </div>
      <div className="container input-group search-input mt-4 mb-5">
        <input
          type="text"
          className="form-control border border-dark"
          placeholder="Enter your requirements..."
          aria-label="Enter your requirements..."
          value={searchQuery} // Controlled input for search
          onChange={(e) => setSearchQuery(e.target.value)} // Update search query
        />
        <button
          className="btn bg-light input-group-append"
          type="button"
          onClick={handleSearch} // Trigger NLP search
        >
          <i className="fa fa-search"></i>
        </button>
      </div>
      <div className="container">
        <div className="row justify-content-center">
          <span className="findby text-center">Find by</span>
          <span className="col-2">
            <select
              id="designs"
              className="form-select find-select"
              onChange={(e) => setDesign(e.target.value)}
            >
              <option value="">Designs</option>
              <option value="Bungalow">Bungalow</option>
              <option value="Villa">Villa</option>
              <option value="Building">Buildings</option>
              <option value="Complex">Complex</option>
              <option value="Penthouse">Penthouse</option>
              <option value="Farmhouses">Farmhouses</option>
              <option value="Cabin">Cabin</option>
            </select>
          </span>
          <span className="col-2">
            <select
              id="price"
              className="form-select find-select"
              onChange={(e) => setPrice(e.target.value)}
            >
              <option value="">Price Range</option>
              <option value="10-20 lakhs">10-20 lakhs</option>
              <option value="20-30 lakhs">20-30 lakhs</option>
              <option value="30-40 lakhs">30-40 lakhs</option>
              <option value="40-50 lakhs">40-50 lakhs</option>
              <option value="50-70 lakhs">50-70 lakhs</option>
              <option value="70-100 lakhs">70-100 lakhs</option>
              <option value="100-500 lakhs">Above 1 Cr</option>
            </select>
          </span>
          <span className="col-2">
            <select
              id="area"
              className="form-select find-select"
              onChange={(e) => setArea(e.target.value)}
            >
              <option value="">Area Sq/Ft</option>
              <option value="500-1000 sq/ft">500-1000 sq/ft</option>
              <option value="1000-1500 sq/ft">1000-1500 sq/ft</option>
              <option value="1500-2500 sq/ft">1500-2000 sq/ft</option>
              <option value="2000-2500 sq/ft">2000-2500 sq/ft</option>
              <option value="2500-3000 sq/ft">2500-3000 sq/ft</option>
              <option value="3000-4000 sq/ft">3000-4000 sq/ft</option>
              <option value="4000-5000 sq/ft">4000-5000 sq/ft</option>
            </select>
          </span>
          <button
            id="findButton"
            className="col-1 btn btn-outline-light find-btn"
            onClick={filterHouses}
          >
            Find
          </button>
        </div>
      </div>
      <div className="container">
        <h3 className="pt-5">Popular Designs</h3>
        <div
          className="row gx-0 gap-5 mt-3 mx-auto justify-content-center"
          id="houseList"
        >
          {renderHouseList()}
        </div>
      </div>
    </main>
  );
};

export default Home;
