import React, { useState, useEffect } from "react";
import "./style.css";
import { Link } from "react-router-dom";

const Home = () => {
  const [houses, setHouses] = useState([]);
  const [filteredHouses, setFilteredHouses] = useState([]);
  const [design, setDesign] = useState("");
  const [price, setPrice] = useState("");
  const [area, setArea] = useState("");

  useEffect(() => {
    fetch("/api/houses")
      .then((response) => response.json())
      .then((data) => {
        if (Array.isArray(data)) {
          setHouses(data);
          setFilteredHouses(data); // Initialize with all houses
        } else {
          console.error("Unexpected data format:", data);
        }
      })
      .catch((error) => console.error("Fetch error:", error));
  }, []);
  

  const filterHouses = () => {
    const filtered = houses.filter((house) => {
      const priceRange = house.price && house.price.split('-');
      const minPrice = priceRange && parseInt(priceRange[0].replace(/\D/g, ''));
      const maxPrice = priceRange && parseInt(priceRange[1].replace(/\D/g, ''));

      const areaRange = house.area && house.area.split('-');
      const minArea = areaRange && parseInt(areaRange[0].replace(/\D/g, ''));
      const maxArea = areaRange && parseInt(areaRange[1].replace(/\D/g, ''));

      return (
        (design === "" || house.name === design) &&
        (price === "" || (minPrice && maxPrice && minPrice <= parseInt(price) && maxPrice >= parseInt(price))) &&
        (area === "" || (minArea && maxArea && minArea <= parseInt(area) && maxArea >= parseInt(area)))
      );
    });
    setFilteredHouses(filtered);
  };

  const renderHouseList = () => {
    if (!filteredHouses || filteredHouses.length === 0) {
      return <p>No houses available.</p>;
    }
    
    return filteredHouses.map((house, index) => (
      <div key={index} className="col-sm-4 col-md-5 col-lg-3 col-xl-3 card">
        <Link
          to={`/houseDetails/${house._id}`}
          className="text-reset text-decoration-none"
          target="_blank"
        >
          <img src={house.img} className="card-img-top" alt={house.name} />
          <div className="card-body">
            <div className="card-content">
              <h5>{house.name}</h5>
              <p>{house.desp}</p>
            </div>
            <div className="card-btn text-end">
              <button className="btn btn-outline-dark">Visit</button>
            </div>
          </div>
        </Link>
      </div>
    ));
  };
  

  return (
    <main className="col-sm-12 col-md-12 col-lg-12 col-xl-12 main p-5">
      <div className="col-sm-12 col-md-12 col-lg-12 col-xl-12 text-center title-text">
        A complete solution for Planning & Designing your dream Home
      </div>
      <div className="container input-group search-input mt-4 mb-5">
        <input
          type="text"
          className="form-control border border-dark"
          placeholder="Enter your requirements..."
          aria-label="Enter your requirements..."
          aria-describedby="button-addon2"
        />
        <button
          className="btn bg-light input-group-append"
          type="button"
          id="button-addon2"
        >
          <i className="fa fa-search"></i>
        </button>
      </div>
      <div className="col-sm-12 col-md-12 col-lg-12 col-xl-12 px-4">
        <div className="row justify-content-center col-sm-12 col-md-12 col-lg-12 col-xl-12">
          <span className="col-sm-12 col-md-12 col-lg-12 col-xl-12 findby text-center">
            Find by
          </span>
          <span className="col-sm-2 col-md-2 col-lg-2 col-xl-2">
            <select
              id="designs"
              className="form-select find-select"
              aria-label="Default select example"
              onChange={(e) => setDesign(e.target.value)}
            >
              <option value="">Designs</option>
              <option value="Penthouse">Penthouse</option>
              <option value="Bungalow">Bungalow</option>
              <option value="Farmhouses">Farmhouses</option>
              <option value="Villa">Villa</option>
              <option value="Cabin">Cabin</option>
            </select>
          </span>
          <span className="col-sm-2 col-md-2 col-lg-2 col-xl-2">
            <select
              id="price"
              className="form-select find-select"
              aria-label="Default select example"
              onChange={(e) => setPrice(e.target.value)}
            >
              <option value="">Price Range</option>
              <option value="15-20 lakhs">15-20 lakhs</option>
              <option value="20-25 lakhs">20-25 lakhs</option>
              <option value="25-35 lakhs">25-35 lakhs</option>
              <option value="35-40 lakhs">35-40 lakhs</option>
            </select>
          </span>
          <span className="col-sm-2 col-md-2 col-lg-2 col-xl-2">
            <select
              id="area"
              className="form-select find-select"
              aria-label="Default select example"
              onChange={(e) => setArea(e.target.value)}
            >
              <option value="">Area Sq/Ft</option>
              <option value="300-500 sq/ft">300-500 sq/ft</option>
              <option value="1000-2000 sq/ft">1000-2000 sq/ft</option>
              <option value="1500-2500 sq/ft">1500-2500 sq/ft</option>
              <option value="2000-3000 sq/ft">2000-3000 sq/ft</option>
            </select>
          </span>
          <button
            id="findButton"
            className="col-sm-1 col-md-1 col-lg-1 col-xl-1 btn btn-outline-light find-btn"
            onClick={filterHouses}
          >
            Find
          </button>
        </div>
      </div>

      <div className="container col-sm-12 col-md-12 col-lg-12 col-xl-12">
        <h3 className="pt-5">Popular Designs</h3>
        <div
          className="col-sm-12 col-md-12 col-lg-12 col-xl-12 row gx-0 gap-5 mt-3 mx-auto justify-content-center"
          id="houseList"
        >
          {renderHouseList()}
        </div>
      </div>
    </main>
  );
};

export default Home;
