import React, { useState } from "react";
import "./style.css";
import { Link } from "react-router-dom";
import houses from "./houses";

const Home = () => {
  const [selectedDesign, setSelectedDesign] = useState("Designs");
  const [selectedPrice, setSelectedPrice] = useState("Price Range");
  const [selectedArea, setSelectedArea] = useState("Area Sq/Ft");
  const [filteredHouses, setFilteredHouses] = useState(houses);

  // Function to parse price and area range from the selected option
  const parseRange = (range) => {
    const [min, max] = range.split(" ")[0].split("-").map(Number);
    return { min, max };
  };

  // Function to filter houses based on selected options
  const handleFindClick = () => {
    const filtered = houses.filter((house) => {
      // Check if the house matches the selected design
      const matchesDesign =
        selectedDesign === "Designs" || house.design === selectedDesign;

      // Check if the house matches the selected price range
      const matchesPrice =
        selectedPrice === "Price Range" ||
        (() => {
          const { min, max } = parseRange(selectedPrice);
          return house.price >= min && house.price <= max;
        })();

      // Check if the house matches the selected area range
      const matchesArea =
        selectedArea === "Area Sq/Ft" ||
        (() => {
          const { min, max } = parseRange(selectedArea);
          return house.area >= min && house.area <= max;
        })();

      // Return true if the house matches all selected filters
      return matchesDesign && matchesPrice && matchesArea;
    });

    setFilteredHouses(filtered);
  };

  // Function to render house cards
  const renderHouseList = () => {
    return filteredHouses.map((house, index) => (
      <div key={index} className="col-sm-10 col-md-5 col-lg-3 col-xl-3 card">
        <Link
          to={`/houseDetails/${index}`}
          className="text-reset text-decoration-none"
          target="_blank"
        >
          <img src={house.img} className="card-img-top" alt={house.name} />
          <div className="card-body">
            <div className="card-content">
              <h5>{house.name}</h5>
              <p>{house.desp}</p>
            </div>
            <div className="card-btn text-end">
              <Link
                to={`/houseDetails/${index}`}
                className="btn btn-outline-dark"
                target="_blank"
              >
                Visit
              </Link>
            </div>
          </div>
        </Link>
      </div>
    ));
  };

  return (
    <main className="col-sm-12 col-md-12 col-lg-12 col-xl-12 main p-5">
      <div className="col-sm-12 col-md-12 col-lg-12 col-xl-12 text-center title-text pt-4 px-5">
        A complete solution for Planning & Designing your dream Home
      </div>
      <div className="container input-group input-group-lg mt-4 mb-5 px-5">
        <input
          type="text"
          className="form-control border border-dark py-3"
          placeholder="Enter your requirements..."
          aria-label="Enter your requirements..."
          aria-describedby="button-addon2"
        />
        <button className="btn btn-light" type="button" id="button-addon">
          <i className="fa fa-search"></i>
        </button>
      </div>
      <div className="col-sm-12 col-md-12 col-lg-12 col-xl-12 d-flex justify-content-center px-4 gap-5">
        <span className="col-sm-1 col-md-1 col-lg-1 col-xl-1 fs-3">
          Find by
        </span>
        <span className="col-sm-2 col-md-2 col-lg-2 col-xl-2">
          <select
            id="designs"
            className="form-select"
            aria-label="Default select example"
            value={selectedDesign}
            onChange={(e) => setSelectedDesign(e.target.value)}
          >
            <option>Designs</option>
            <option value="Penthouse">Penthouse</option>
            <option value="Bungalow">Bungalow</option>
            <option value="Farmhouses">Farmhouses</option>
            <option value="Villa">Villa</option>
            <option value="Cabin">Cabin</option>
          </select>
        </span>
        <span className="col-sm-2 col-md-2 col-lg-2 col-xl-2">
          <select
            id="price"
            className="form-select"
            aria-label="Default select example"
            value={selectedPrice}
            onChange={(e) => setSelectedPrice(e.target.value)}
          >
            <option>Price Range</option>
            <option value="15-20 lakhs">15-20 lakhs</option>
            <option value="20-25 lakhs">20-25 lakhs</option>
            <option value="25-35 lakhs">25-35 lakhs</option>
            <option value="35-40 lakhs">35-40 lakhs</option>
          </select>
        </span>
        <span className="col-sm-2 col-md-2 col-lg-2 col-xl-2">
          <select
            id="area"
            className="form-select"
            aria-label="Default select example"
            value={selectedArea}
            onChange={(e) => setSelectedArea(e.target.value)}
          >
            <option>Area Sq/Ft</option>
            <option value="300-500 sq/ft">300-500 sq/ft</option>
            <option value="1000-2000 sq/ft">1000-2000 sq/ft</option>
            <option value="1500-2500 sq/ft">1500-2500 sq/ft</option>
            <option value="2000-3000 sq/ft">2000-3000 sq/ft</option>
          </select>
        </span>
        <button
          id="findButton"
          className="col-sm-1 col-md-1 col-lg-1 col-xl-1 btn btn-outline-light find-btn"
          onClick={handleFindClick}
        >
          Find
        </button>
      </div>

      <div id="houseContainer" className="row"></div>

      <div className="container col-sm-12 col-md-12 col-lg-12 col-xl-12">
        <h3 className="pt-5">Popular Designs</h3>
        <div
          className="row gx-0 gap-5 mt-3 mx-auto justify-content-center"
          id="houseList"
        >
          {renderHouseList()}
        </div>
      </div>
    </main>
  );
};

export default Home;
