import React, { useState, useEffect } from "react";
import "./style.css";
import houses from "./houses";

const Home = () => {
  // Initialize filteredHouses as an array
  const [filteredHouses, setFilteredHouses] = useState(houses);
  const [filters, setFilters] = useState({
    design: "Designs",
    price: "Price Range",
    area: "Area Sq/Ft",
  });

  useEffect(() => {
    applyFilters();
  }, [filters]);

  const handleFilterChange = (e) => {
    const { id, value } = e.target;
    setFilters((prevFilters) => ({
      ...prevFilters,
      [id]: value,
    }));
  };

  const applyFilters = () => {
    const filtered = houses.filter((house) => {
      return (
        (filters.design === "Designs" || house.name.includes(filters.design)) &&
        (filters.price === "Price Range" || house.price.includes(filters.price)) &&
        (filters.area === "Area Sq/Ft" || house.area.includes(filters.area))
      );
    });
    // Ensure setFilteredHouses is called with an array
    setFilteredHouses(filtered);
  };

  const houseInfo = (index) => {
    window.location.href = `houseDetails.html?houseIndex=${index}`;
  };

  const renderHouseList = () => {
    // Check if filteredHouses is indeed an array
    if (!Array.isArray(filteredHouses)) {
      return <div>Error: filteredHouses is not an array</div>;
    }
    return filteredHouses.map((house, index) => (
      <div
        className="col-sm-10 col-md-5 col-lg-3 col-xl-3 card"
        key={index}
        onClick={() => houseInfo(index)}
      >
        <img src={house.img} className="card-img-top" alt={house.name} />
        <div className="card-body">
          <div className="card-content">
            <h5>{house.name}</h5>
            <p>{house.desp}</p>
          </div>
          <div className="card-btn text-end">
            <button
              className="btn btn-outline-dark"
              onClick={(e) => {
                e.stopPropagation(); // Prevent triggering card onClick
                houseInfo(index);
              }}
            >
              Visit
            </button>
          </div>
        </div>
      </div>
    ));
  };

  return (
    <main className="col-sm-12 col-md-12 col-lg-12 col-xl-12 main p-5">
      <div className="col-sm-12 col-md-12 col-lg-12 col-xl-12 text-center title-text pt-5">
        A complete solution for Planning & Designing your dream Home
      </div>
      <div className="container input-group input-group-lg mt-4 mb-5 px-5">
        <input
          type="text"
          className="form-control border border-dark py-3"
          placeholder="Enter your requirements..."
          aria-label="Enter your requirements..."
          aria-describedby="button-addon2"
        />
        <button className="btn btn-outline-dark" type="button" id="button-addon">
          <i className="fa fa-search"></i>
        </button>
      </div>
      <div className="col-sm-12 col-md-12 col-lg-12 col-xl-12 d-flex justify-content-center px-4 gap-5">
        <span className="col-sm-1 col-md-1 col-lg-1 col-xl-1 fs-3">Find by</span>
        <span className="col-sm-2 col-md-2 col-lg-2 col-xl-2">
          <select
            id="design"
            className="form-select"
            aria-label="Default select example"
            onChange={handleFilterChange}
          >
            <option value="Designs">Designs</option>
            <option value="Penthouse">Penthouse</option>
            <option value="Bungalow">Bungalow</option>
            <option value="Farmhouses">Farmhouses</option>
            <option value="Villa">Villa</option>
            <option value="Cabin">Cabin</option>
          </select>
        </span>
        <span className="col-sm-2 col-md-2 col-lg-2 col-xl-2">
          <select
            id="price"
            className="form-select"
            aria-label="Default select example"
            onChange={handleFilterChange}
          >
            <option value="Price Range">Price Range</option>
            <option value="15-20 lakhs">15-20 lakhs</option>
            <option value="20-25 lakhs">20-25 lakhs</option>
            <option value="25-35 lakhs">25-35 lakhs</option>
            <option value="35-40 lakhs">35-40 lakhs</option>
          </select>
        </span>
        <span className="col-sm-2 col-md-2 col-lg-2 col-xl-2">
          <select
            id="area"
            className="form-select"
            aria-label="Default select example"
            onChange={handleFilterChange}
          >
            <option value="Area Sq/Ft">Area Sq/Ft</option>
            <option value="300-500 sq/ft">300-500 sq/ft</option>
            <option value="1000-2000 sq/ft">1000-2000 sq/ft</option>
            <option value="1500-2500 sq/ft">1500-2500 sq/ft</option>
            <option value="2000-3000 sq/ft">2000-3000 sq/ft</option>
          </select>
        </span>
        <button
          id="findButton"
          className="col-sm-1 col-md-1 col-lg-1 col-xl-1 btn btn-outline-dark find-btn"
          onClick={applyFilters}
        >
          Find
        </button>
      </div>

      <div id="houseContainer" className="row"></div>

      <div className="container col-sm-12 col-md-12 col-lg-12 col-xl-12">
        <h3 className="pt-5">Popular Designs</h3>
        <div
          className="row gx-0 gap-5 mt-3 mx-auto justify-content-center"
          id="houseList"
        >
          {renderHouseList()}
        </div>
      </div>
    </main>
  );
};

export default Home;
