import React, { useState, useEffect } from "react";
import "./navbar.css";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faSun, faMoon } from "@fortawesome/free-solid-svg-icons";
import logo from "../assets/logo.png";
import home from "../assets/home.png";
import services from "../assets/services.png";
import about from "../assets/about.png";
import contact from "../assets/contact.png";
import { Link } from "react-router-dom";

const Navbar = () => {
  const [theme, setTheme] = useState(localStorage.getItem("theme") || "dark");

  useEffect(() => {
    document.body.className = theme;
    localStorage.setItem("theme", theme);
  }, [theme]);

  const toggleTheme = () => {
    setTheme((prevTheme) => (prevTheme === "dark" ? "light" : "dark"));
  };

  return (
    <div className="nav-bar">
      <div className="nav-tabs">
        <div className="logo-div">
          <Link className="logo" aria-current="page" to="/">
            <img src={logo} className="img-fluid" alt="Logo" />
          </Link>
        </div>
        <div className="nav">
          <Link className="nav-icons" aria-current="page" title="Home" to="/">
            <img src={home} className="icons" alt="Home" />
          </Link>
          <Link className="nav-icons" aria-current="page" title="Services" to="/services">
            <img src={services} className="icons" alt="Services" />
          </Link>
          <Link className="nav-icons" aria-current="page" title="About" to="/about">
            <img src={about} className="icons" alt="About" />
          </Link>
          <Link className="nav-icons" aria-current="page" title="Contact" to="/contact">
            <img src={contact} className="icons" alt="Contact" />
          </Link>
        </div>
        <div className="mode">
          <button onClick={toggleTheme} className="theme-toggle-btn">
            <FontAwesomeIcon icon={theme === "dark" ? faSun : faMoon} />
          </button>
        </div>
      </div>
    </div>
  );
};

export default Navbar;
