import React, { useState, useEffect } from "react";
import "./style.css";
import { Link } from "react-router-dom";

const Home = () => {
  const [houses, setHouses] = useState([]);
  const [filteredHouses, setFilteredHouses] = useState([]);
  const [categories, setCategories] = useState({}); // State for grouped categories
  const [expandedCategories, setExpandedCategories] = useState({}); // Track which categories are expanded

  // Fetch houses from backend API
  useEffect(() => {
    fetch("/api/houses")
      .then((response) => response.json())
      .then((data) => {
        if (Array.isArray(data)) {
          setHouses(data);
          setFilteredHouses(data);
          groupByCategory(data); // Group houses by design type
        } else {
          console.error("Unexpected data format:", data);
        }
      })
      .catch((error) => console.error("Fetch error:", error));
  }, []);

  // Group houses by category (design)
  const groupByCategory = (data) => {
    const grouped = data.reduce((acc, house) => {
      const design = house.name || "Uncategorized"; // Group by name or set 'Uncategorized' if missing
      if (!acc[design]) {
        acc[design] = [];
      }
      acc[design].push(house);
      return acc;
    }, {});
    setCategories(grouped); // Set grouped categories
  };

  // Handle view more or collapse in each category
  const toggleExpandCategory = (design) => {
    setExpandedCategories((prevState) => ({
      ...prevState,
      [design]: !prevState[design],
    }));
  };

  // Render house cards with expand/collapse functionality
  const renderHouseList = (design, houses) => {
    const isExpanded = expandedCategories[design]; // Check if this category is expanded
    const visibleHouses = isExpanded ? houses : houses.slice(0, 6); // Show 6 by default or all if expanded

    return (
      <div key={design} className="category-section mb-5">
        <h3>{design}</h3>
        <div className="row gx-0 gap-5 mt-3 mx-auto justify-content-center">
          {visibleHouses.map((house, index) => (
            <div key={index} className="col-sm-4 col-md-5 col-lg-3 col-xl-3 card">
              <Link to={`/houseDetails/${house._id}`} className="text-reset text-decoration-none">
                <img
                  src={house.images?.exterior?.[0] || "default-image.jpg"}
                  className="card-img-top"
                  alt={house.name}
                />
                <div className="card-body">
                  <h5>{house.name}</h5>
                  <p>{house.desp}</p>
                </div>
              </Link>
            </div>
          ))}
        </div>

        {/* View More / View Less Button */}
        <div className="text-end">
          <button
            className="btn btn-outline-dark mt-3"
            onClick={() => toggleExpandCategory(design)}
          >
            {isExpanded ? "View Less" : "View More"}
          </button>
        </div>
      </div>
    );
  };

  return (
    <main className="col-sm-12 col-md-12 col-lg-12 col-xl-12 main p-5">
      <div className="text-center title-text">
        A complete solution for Planning & Designing your dream Home
      </div>
      <div className="container">
        {Object.keys(categories).map((design) =>
          renderHouseList(design, categories[design])
        )}
      </div>
    </main>
  );
};

export default Home;
