import React, { useState, useEffect } from "react";
import { useParams, Link } from "react-router-dom";
import "./style.css";

const ViewByCategory = () => {
  const { category } = useParams(); // Get the category from the URL parameters
  const [houses, setHouses] = useState([]);

  // Fetch houses for the specific category
  useEffect(() => {
    fetch(`/api/houses/category/${category}`)
      .then((response) => response.json())
      .then((data) => setHouses(data))
      .catch((error) => console.error("Error fetching data:", error));
  }, [category]);

  return (
    <main className="container">
      <div className="text-center mt-4 mb-5">
        <h2>{category} Designs</h2>
      </div>
      <div className="row justify-content-center">
        {houses.length > 0 ? (
          houses.map((house, index) => (
            <div key={index} className="col-sm-4 col-md-5 col-lg-3 col-xl-3 card">
              <Link
                to={`/houseDetails/${house._id}`}
                className="text-reset text-decoration-none"
                target="_blank"
              >
                <img
                  src={house.images?.exterior?.[0] || "default-image.jpg"}
                  className="card-img-top"
                  alt={house.name}
                />
                <div className="card-body">
                  <h5>{house.name}</h5>
                  <p>{house.desp}</p>
                  <button className="btn btn-outline-dark">Visit</button>
                </div>
              </Link>
            </div>
          ))
        ) : (
          <p className="text-center">No houses available in this category.</p>
        )}
      </div>
    </main>
  );
};

export default ViewByCategory;
