import React, { useState, useEffect } from "react";
import "./style.css";
import { Link } from "react-router-dom";

const Home = () => {
  const [houses, setHouses] = useState([]);
  const [viewMoreCount, setViewMoreCount] = useState({}); // Track expanded view for each category

  // Fetch houses from backend API
  useEffect(() => {
    fetch("/api/houses")
      .then((response) => response.json())
      .then((data) => {
        if (Array.isArray(data)) {
          setHouses(data);
        } else {
          console.error("Unexpected data format:", data);
        }
      })
      .catch((error) => console.error("Fetch error:", error));
  }, []);

  // Handle expanding the view for a category (show more designs)
  const handleViewMore = (category) => {
    setViewMoreCount((prevState) => ({
      ...prevState,
      [category]: prevState[category] ? prevState[category] + 14 : 20,
    }));
  };

  // Function to render category sections with cards
  const renderCategorySection = (category, displayCount) => {
    const filteredHouses = houses.filter(
      (house) => house.category === category
    );
    const displayHouses = filteredHouses.slice(0, displayCount);

    return (
      <div key={category} className="category-section mb-5">
        <h4>{category}</h4>
        <div className="row">
          {displayHouses.map((house, index) => (
            <div key={index} className="col-sm-4 col-md-5 col-lg-3 col-xl-3 card">
              <Link
                to={`/houseDetails/${house._id}`}
                className="text-reset text-decoration-none"
                target="_blank"
              >
                <img
                  src={house.images?.exterior?.[0] || "default-image.jpg"}
                  className="card-img-top"
                  alt={house.name}
                />
                <div className="card-body">
                  <h5>{house.name}</h5>
                  <p>{house.desp}</p>
                  <button className="btn btn-outline-dark">Visit</button>
                </div>
              </Link>
            </div>
          ))}
        </div>
        {filteredHouses.length > 6 && (
          <div className="d-flex justify-content-end mt-3">
            {displayCount < filteredHouses.length && (
              <button
                className="btn btn-primary me-2"
                onClick={() => handleViewMore(category)}
              >
                View More
              </button>
            )}
            <Link to={`/category/${category}`}>
              <button className="btn btn-secondary">View All</button>
            </Link>
          </div>
        )}
      </div>
    );
  };

  return (
    <main className="col-sm-12 col-md-12 col-lg-12 col-xl-12 main p-5">
      <div className="text-center title-text">
        A complete solution for Planning & Designing your dream Home
      </div>
      <div className="container">
        <h3 className="pt-5">Popular Designs</h3>
        <div className="container mt-4">
          {["Villa", "Bungalow", "Apartment", "Complex"].map((category) =>
            renderCategorySection(category, viewMoreCount[category] || 6)
          )}
        </div>
      </div>
    </main>
  );
};

export default Home;
